import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated }) => {
  const [linksDropdownOpen, setLinksDropdownOpen] = useState(false);
  const [links, setLinks] = useState([]);
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const response = await fetch('https://test3.cola-workeu.com/links/links');
        const data = await response.json();
        setLinks(data);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();

    // Add scroll effect
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 0);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleLinksDropdown = () => {
    setLinksDropdownOpen(!linksDropdownOpen);
  };

  const NavLink = ({ to, children }) => (
    <Link
      to={to}
      className="relative group px-4 py-2 text-gray-300 hover:text-white transition-all duration-300"
    >
      <span className="relative z-10">{children}</span>
      <span className="absolute inset-x-0 bottom-0 h-0.5 bg-gradient-to-r from-blue-500 to-purple-500 transform scale-x-0 group-hover:scale-x-100 transition-transform duration-300"></span>
    </Link>
  );

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 transition-all duration-300 ${
      isScrolled 
        ? 'bg-gray-900/95 backdrop-blur-lg shadow-lg' 
        : 'bg-gray-900/50 backdrop-blur-sm'
    }`}>
      <div className="container mx-auto">
        <div className="relative flex justify-between items-center py-4 px-6">
          {/* Gradient Border Bottom */}
          <div className="absolute bottom-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-gray-700 to-transparent"></div>

          {/* Links Dropdown */}
          <div className="relative group">
            <button
              onClick={toggleLinksDropdown}
              className="flex items-center space-x-2 px-4 py-2 rounded-lg bg-gray-800/50 hover:bg-gray-700/50 text-gray-300 hover:text-white transition-all duration-300 border border-gray-700/50 hover:border-gray-600/50"
            >
              <span>Links</span>
              <svg 
                className={`w-4 h-4 transition-transform duration-300 ${linksDropdownOpen ? 'rotate-180' : ''}`}
                fill="none" 
                stroke="currentColor" 
                viewBox="0 0 24 24"
              >
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
              </svg>
            </button>
            {linksDropdownOpen && (
              <div className="absolute left-0 mt-2 w-64 overflow-hidden">
                <div className="relative">
                  {/* Backdrop blur and gradient background */}
                  <div className="absolute inset-0 bg-gradient-to-br from-gray-800/95 to-gray-900/95 backdrop-blur-xl"></div>
                  {/* Gradient border */}
                  <div className="absolute inset-0 border border-gray-700/50 rounded-lg"></div>
                  <ul className="relative divide-y divide-gray-700/50">
                    {links.map(link => (
                      <li key={link._id}>
                        <a
                          href={link.url}
                          className="block px-4 py-3 text-gray-300 hover:text-white transition-colors duration-300 hover:bg-gray-700/50"
                        >
                          <div className="flex items-center space-x-2">
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                            </svg>
                            <span className="truncate">{link.url}</span>
                          </div>
                        </a>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            )}
          </div>

          {/* Logo */}
          <Link to="/" className="absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2">
            <h1 className="text-4xl font-bold">
              <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-400 to-purple-400">
                Chat*S
              </span>
            </h1>
          </Link>

          {/* Navigation */}
          <nav className="flex-1 flex justify-end">
            <ul className="flex items-center space-x-2">
              {isAuthenticated ? (
                <>
                  <li><NavLink to="/">Dashboard</NavLink></li>
                  <li><NavLink to="/add-chat">Add Channels</NavLink></li>
                  <li><NavLink to="/join-chat">Join Chat</NavLink></li>
                  <li>
                    <NavLink to="/add-chats">
                      <span className="flex items-center space-x-1">
                        <span>Add Chats</span>
                        <span className="flex h-2 w-2">
                          <span className="animate-ping absolute inline-flex h-2 w-2 rounded-full bg-purple-400 opacity-75"></span>
                          <span className="relative inline-flex rounded-full h-2 w-2 bg-purple-500"></span>
                        </span>
                      </span>
                    </NavLink>
                  </li>
                </>
              ) : (
                <>
                  <li>
                    <Link
                      to="/login"
                      className="px-6 py-2 rounded-lg bg-gradient-to-r from-blue-500 to-purple-500 text-white font-medium hover:shadow-lg hover:shadow-blue-500/25 transition-all duration-300"
                    >
                      Login
                    </Link>
                  </li>
                  <li>
                    <Link
                      to="/register"
                      className="px-6 py-2 rounded-lg bg-gray-800 text-gray-300 border border-gray-700 hover:bg-gray-700 hover:text-white transition-all duration-300"
                    >
                      Register
                    </Link>
                  </li>
                </>
              )}
            </ul>
          </nav>
        </div>
      </div>
    </header>
  );
};

export default Header;
