import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated }) => {
  const [linksDropdownOpen, setLinksDropdownOpen] = useState(false);
  const [links, setLinks] = useState([]);
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const response = await fetch('https://test3.cola-workeu.com/links/links');
        const data = await response.json();
        setLinks(data);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();

    const handleScroll = () => {
      setScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <header className={`fixed top-0 left-0 right-0 z-50 transition-all duration-500 ${
      scrolled ? 'bg-gray-900/80 backdrop-blur-lg shadow-lg' : 'bg-gray-900/30 backdrop-blur-sm'
    }`}>
      {/* Top gradient border */}
      <div className="absolute top-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-indigo-500/50 to-transparent" />
      
      <div className="container mx-auto">
        <div className="relative flex justify-between items-center py-4 px-6">
          {/* Links Dropdown */}
          <div className="relative group">
            <button
              onClick={() => setLinksDropdownOpen(!linksDropdownOpen)}
              className="relative px-4 py-2 rounded-xl text-gray-300 hover:text-white transition-all duration-300 overflow-hidden group"
            >
              {/* Background animation */}
              <span className="absolute inset-0 bg-gradient-to-r from-indigo-500/20 to-purple-500/20 translate-y-10 group-hover:translate-y-0 transition-transform duration-300" />
              
              {/* Content */}
              <span className="relative flex items-center gap-2">
                Links
                <svg 
                  className={`w-4 h-4 transform transition-transform duration-300 ${linksDropdownOpen ? 'rotate-180' : ''}`} 
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                </svg>
              </span>
            </button>
            
            {linksDropdownOpen && (
              <div className="absolute left-0 mt-2 w-64 animate-fadeIn">
                <div className="relative rounded-xl overflow-hidden">
                  {/* Gradient border */}
                  <div className="absolute inset-0 p-[1px] bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 rounded-xl">
                    <div className="absolute inset-0 bg-gray-900/95 backdrop-blur-xl rounded-xl" />
                  </div>
                  
                  {/* Links list */}
                  <ul className="relative divide-y divide-gray-700/30">
                    {links.map(link => (
                      <li key={link._id}>
                        <a
                          href={link.url}
                          className="flex items-center gap-3 px-4 py-3 text-gray-300 hover:text-white transition-colors duration-300 hover:bg-white/5 group"
                        >
                          {/* Icon */}
                          <span className="w-8 h-8 rounded-lg bg-gradient-to-br from-indigo-500/10 to-purple-500/10 flex items-center justify-center border border-white/5 group-hover:border-white/10 transition-colors duration-300">
                            <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                            </svg>
                          </span>
                          
                          {/* URL text */}
                          <span className="truncate">{link.url}</span>
                        </a>
                      </li>
                    ))}
                  </ul>
                </div>
              </div>
            )}
          </div>

          {/* Logo */}
          <Link 
            to="/" 
            className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 group"
          >
            <div className="relative">
              {/* Glow effect */}
              <div className="absolute -inset-1 bg-gradient-to-r from-indigo-500 via-purple-500 to-pink-500 rounded-lg blur opacity-0 group-hover:opacity-75 transition duration-500" />
              <h1 className="relative text-4xl font-black tracking-tight">
                <span className="bg-clip-text text-transparent bg-gradient-to-r from-indigo-400 via-purple-400 to-pink-400">
                  Chat*S
                </span>
              </h1>
            </div>
          </Link>

          {/* Navigation */}
          <nav>
            <ul className="flex items-center gap-2">
              {isAuthenticated ? (
                <>
                  {[
                    { to: "/", label: "Dashboard" },
                    { to: "/add-chat", label: "Add Channels" },
                    { to: "/join-chat", label: "Join Chat" },
                    { to: "/add-chats", label: "Add Chats", isSpecial: true }
                  ].map((item) => (
                    <li key={item.to}>
                      <Link
                        to={item.to}
                        className="relative px-4 py-2 rounded-xl text-gray-300 hover:text-white transition-all duration-300 overflow-hidden group"
                      >
                        {/* Hover background */}
                        <span className="absolute inset-0 bg-gradient-to-r from-indigo-500/20 to-purple-500/20 translate-y-10 group-hover:translate-y-0 transition-transform duration-300" />
                        
                        {/* Content */}
                        <span className="relative flex items-center gap-2">
                          {item.label}
                          {item.isSpecial && (
                            <span className="flex h-2 w-2">
                              <span className="animate-ping absolute inline-flex h-2 w-2 rounded-full bg-purple-400 opacity-75" />
                              <span className="relative inline-flex rounded-full h-2 w-2 bg-purple-500" />
                            </span>
                          )}
                        </span>
                      </Link>
                    </li>
                  ))}
                </>
              ) : (
                <>
                  <li>
                    <Link
                      to="/login"
                      className="px-6 py-2 rounded-xl bg-gradient-to-r from-indigo-500 to-purple-500 text-white font-medium hover:shadow-lg hover:shadow-indigo-500/25 transition-all duration-300"
                    >
                      Login
                    </Link>
                  </li>
                  <li>
                    <Link
                      to="/register"
                      className="px-6 py-2 rounded-xl bg-white/5 border border-white/10 text-gray-300 hover:bg-white/10 hover:text-white hover:border-white/20 transition-all duration-300"
                    >
                      Register
                    </Link>
                  </li>
                </>
              )}
            </ul>
          </nav>
        </div>
      </div>

      {/* Bottom gradient border */}
      <div className="absolute bottom-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-purple-500/50 to-transparent" />
    </header>
  );
};

export default Header;
