import React, { useEffect, useState, useCallback } from 'react';
import moment from 'moment-timezone';
import { fetchKeywords, addKeyword, deleteKeyword, fetchMessages } from '../components/api';

const Dashboard = () => {
  const [keywords, setKeywords] = useState([]);
  const [newKeyword, setNewKeyword] = useState('');
  const [messagesWithKeywords, setMessagesWithKeywords] = useState([]);
  const [chatId, setChatId] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [filterKeyword, setFilterKeyword] = useState('');
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const [messagesPerPage, setMessagesPerPage] = useState(10);

  useEffect(() => {
    const loadInitialData = async () => {
      try {
        const fetchedKeywords = await fetchKeywords();
        setKeywords(fetchedKeywords);

        const fetchedMessages = await fetchMessages();
        filterMessages(fetchedMessages, fetchedKeywords);
      } catch (error) {
        console.error('Error fetching initial data:', error);
      }
    };

    loadInitialData();
  }, []);

  const filterMessages = useCallback((messages, keywords) => {
    const filteredMessages = messages.filter(message => {
      const messageDate = moment(message.timestamp);
      const startDateTime = startDate && startTime ? moment.tz(`${startDate} ${startTime}`, 'Europe/Kiev') : null;
      const endDateTime = endDate && endTime ? moment.tz(`${endDate} ${endTime}`, 'Europe/Kiev') : null;

      return (
        (!startDateTime || messageDate.isSameOrAfter(startDateTime)) &&
        (!endDateTime || messageDate.isSameOrBefore(endDateTime)) &&
        (!filterKeyword || (message.text && message.text.includes(filterKeyword))) &&
        keywords.some(keyword => message.text && message.text.includes(keyword.keyword))
      );
    });
    setMessagesWithKeywords(filteredMessages);
  }, [startDate, startTime, endDate, endTime, filterKeyword, keywords]);

  const handleAddKeyword = useCallback(async (event) => {
    event.preventDefault();
    if (newKeyword.trim() === '') {
      console.error('Ошибка: Ключевое слово не должно быть пустым');
      return;
    }
  
    try {
      const addedKeyword = await addKeyword(newKeyword); // Передаем строку, а не объект
      setKeywords([...keywords, addedKeyword]);
      setNewKeyword('');
      filterMessages(messagesWithKeywords, [...keywords, addedKeyword]);
    } catch (error) {
      console.error('Error adding keyword:', error);
    }
  }, [newKeyword, keywords, messagesWithKeywords]);

  const handleDeleteKeyword = useCallback(async (id) => {
    try {
      await deleteKeyword(id);
      const updatedKeywords = keywords.filter(keyword => keyword._id !== id);
      setKeywords(updatedKeywords);
      filterMessages(messagesWithKeywords, updatedKeywords);
    } catch (error) {
      console.error('Error deleting keyword:', error);
    }
  }, [keywords, messagesWithKeywords]);

  const handleFilter = useCallback(async () => {
    const query = new URLSearchParams();
    if (chatId) query.append('chatId', chatId);
    if (startDate && endDate) {
      query.append('startDate', startDate);
      query.append('endDate', endDate);
    }
    if (filterKeyword) query.append('keyword', filterKeyword);

    const filteredMessages = await fetchMessages(query.toString());
    filterMessages(filteredMessages, keywords);
  }, [chatId, startDate, endDate, filterKeyword, keywords]);

  const indexOfLastMessage = currentPage * messagesPerPage;
  const indexOfFirstMessage = indexOfLastMessage - messagesPerPage;
  const currentMessages = messagesWithKeywords.slice(indexOfFirstMessage, indexOfLastMessage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 p-6">
      <div className="max-w-7xl mx-auto space-y-8">
        {/* Header */}
        <div className="text-center">
          <h2 className="text-4xl font-bold text-white mb-2">Dashboard</h2>
          <div className="h-1 w-24 bg-blue-500 mx-auto rounded-full"></div>
        </div>

        {/* Keywords Section */}
        <div className="bg-gray-800 rounded-xl p-6 shadow-lg">
          <h3 className="text-2xl font-semibold text-white mb-6 flex items-center">
            <span className="bg-blue-500 w-2 h-8 rounded mr-3"></span>
            Keywords
          </h3>
          
          {/* Keywords List */}
          <div className="grid gap-3 mb-6">
            {keywords.map((keyword) => (
              <div key={keyword._id} 
                   className="flex justify-between items-center bg-gray-700 p-4 rounded-lg hover:bg-gray-600 transition-all duration-300">
                <span className="text-white font-medium">{keyword.keyword}</span>
                <div className="flex items-center space-x-4">
                  <span className="text-blue-400 bg-blue-400/10 px-3 py-1 rounded-full text-sm">
                    {keyword.count}
                  </span>
                  <button
                    onClick={() => handleDeleteKeyword(keyword._id)}
                    className="text-red-400 hover:text-red-300 transition-colors duration-300"
                  >
                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 7l-.867 12.142A2 2 0 0116.138 21H7.862a2 2 0 01-1.995-1.858L5 7m5 4v6m4-6v6m1-10V4a1 1 0 00-1-1h-4a1 1 0 00-1 1v3M4 7h16" />
                    </svg>
                  </button>
                </div>
              </div>
            ))}
          </div>

          {/* Add Keyword Form */}
          <form onSubmit={handleAddKeyword} className="flex gap-3">
            <input
              type="text"
              placeholder="Add a new keyword"
              value={newKeyword}
              onChange={(e) => setNewKeyword(e.target.value)}
              className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 transition-colors duration-300"
            />
            <button
              type="submit"
              className="bg-blue-500 hover:bg-blue-600 text-white px-6 py-2 rounded-lg font-medium transition-colors duration-300 flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 4v16m8-8H4" />
              </svg>
              Add
            </button>
          </form>
        </div>

        {/* Filters Section */}
        <div className="bg-gray-800 rounded-xl p-6 shadow-lg">
          <h3 className="text-2xl font-semibold text-white mb-6 flex items-center">
            <span className="bg-blue-500 w-2 h-8 rounded mr-3"></span>
            Message Filters
          </h3>
          
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
            <input
              type="text"
              placeholder="Chat ID"
              value={chatId}
              onChange={(e) => setChatId(e.target.value)}
              className="bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 transition-colors duration-300"
            />
            <div className="flex gap-2">
              <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-blue-500 transition-colors duration-300"
              />
              <input
                type="time"
                value={startTime}
                onChange={(e) => setStartTime(e.target.value)}
                className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-blue-500 transition-colors duration-300"
              />
            </div>
            <div className="flex gap-2">
              <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-blue-500 transition-colors duration-300"
              />
              <input
                type="time"
                value={endTime}
                onChange={(e) => setEndTime(e.target.value)}
                className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white focus:outline-none focus:border-blue-500 transition-colors duration-300"
              />
            </div>
          </div>
          
          <div className="flex gap-4">
            <input
              type="text"
              placeholder="Filter by keyword"
              value={filterKeyword}
              onChange={(e) => setFilterKeyword(e.target.value)}
              className="flex-1 bg-gray-700 border-2 border-gray-600 rounded-lg px-4 py-2 text-white placeholder-gray-400 focus:outline-none focus:border-blue-500 transition-colors duration-300"
            />
            <button
              onClick={handleFilter}
              className="bg-blue-500 hover:bg-blue-600 text-white px-8 py-2 rounded-lg font-medium transition-colors duration-300 flex items-center"
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z" />
              </svg>
              Filter
            </button>
          </div>
        </div>

        {/* Messages Section */}
        <div className="bg-gray-800 rounded-xl p-6 shadow-lg">
          <div className="space-y-4">
            {currentMessages.map((message, index) => (
              <div key={index} className="bg-gray-700 rounded-lg p-4 hover:bg-gray-600 transition-all duration-300">
                <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                  <div className="flex items-center gap-3">
                    <div className="w-10 h-10 bg-blue-500 rounded-full flex items-center justify-center">
                      <span className="text-white font-bold">
                        {message.sender?.charAt(0)?.toUpperCase() || 'U'}
                      </span>
                    </div>
                    <div className="flex flex-col">
                      <span className="font-medium text-yellow-400">{message.userId}</span>
                      <span className="text-sm text-blue-400">{message.sender}</span>
                    </div>
                  </div>
                  <span className="text-gray-400 bg-gray-800 px-3 py-1 rounded-full text-sm">
                    {moment.tz(message.timestamp, 'Europe/Kiev').format('YYYY-MM-DD HH:mm:ss')}
                  </span>
                </div>
                <p className="mt-3 text-gray-300 pl-13">{message.text}</p>
              </div>
            ))}
          </div>

          {/* Pagination */}
          <div className="flex justify-between items-center mt-6">
            <button
              onClick={() => paginate(currentPage - 1)}
              disabled={currentPage === 1}
              className={`px-4 py-2 rounded-lg flex items-center ${
                currentPage === 1 
                ? 'bg-gray-700 text-gray-500 cursor-not-allowed' 
                : 'bg-blue-500 hover:bg-blue-600 text-white transition-colors duration-300'
              }`}
            >
              <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7" />
              </svg>
              Previous
            </button>
            
            <span className="text-white bg-gray-700 px-4 py-2 rounded-lg">
              Page {currentPage}
            </span>
            
            <button
              onClick={() => paginate(currentPage + 1)}
              disabled={indexOfLastMessage >= messagesWithKeywords.length}
              className={`px-4 py-2 rounded-lg flex items-center ${
                indexOfLastMessage >= messagesWithKeywords.length
                ? 'bg-gray-700 text-gray-500 cursor-not-allowed'
                : 'bg-blue-500 hover:bg-blue-600 text-white transition-colors duration-300'
              }`}
            >
              Next
              <svg className="w-5 h-5 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 5l7 7-7 7" />
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
