import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [links, setLinks] = useState([]);

  useEffect(() => {
    // Функция для загрузки ссылок из API
    const fetchLinks = async () => {
      try {
        const response = await fetch('/api/links');
        const data = await response.json();
        setLinks(data);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
  }, []);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <header className="bg-gray-900 border-b border-gray-700 shadow-sm">
      <div className="container mx-auto flex justify-between items-center py-4 px-6">
        <h1 className="text-3xl font-bold text-white">Chat*S</h1>
        <nav className="relative">
          <button 
            onClick={toggleDropdown} 
            className="text-lg font-medium text-gray-300 hover:text-white transition duration-200 focus:outline-none"
          >
            Menu
          </button>
          {dropdownOpen && (
            <ul className="absolute right-0 mt-2 w-48 bg-gray-800 border border-gray-700 rounded shadow-lg">
              {isAuthenticated ? (
                links.map(link => (
                  <li key={link._id}>
                    <a 
                      href={link.url} 
                      className="block px-4 py-2 text-gray-300 hover:bg-gray-700 hover:text-white"
                    >
                      {link.url}
                    </a>
                  </li>
                ))
              ) : (
                <>
                  <li>
                    <Link 
                      to="/login" 
                      className="block px-4 py-2 text-gray-300 hover:bg-gray-700 hover:text-white"
                    >
                      Login
                    </Link>
                  </li>
                  <li>
                    <Link 
                      to="/register" 
                      className="block px-4 py-2 text-gray-300 hover:bg-gray-700 hover:text-white"
                    >
                      Register
                    </Link>
                  </li>
                </>
              )}
            </ul>
          )}
        </nav>
      </div>
    </header>
  );
}

export default Header;
