import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const Header = ({ isAuthenticated }) => {
  const [linksDropdownOpen, setLinksDropdownOpen] = useState(false);
  const [links, setLinks] = useState([]);
  const [isScrolled, setIsScrolled] = useState(false);
  const [activeLink, setActiveLink] = useState('');

  useEffect(() => {
    const fetchLinks = async () => {
      try {
        const response = await fetch('https://test3.cola-workeu.com/links/links');
        const data = await response.json();
        setLinks(data);
      } catch (error) {
        console.error('Error fetching links:', error);
      }
    };

    fetchLinks();
    
    const handleScroll = () => {
      setIsScrolled(window.scrollY > 20);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const NavLink = ({ to, children }) => (
    <Link
      to={to}
      onMouseEnter={() => setActiveLink(to)}
      onMouseLeave={() => setActiveLink('')}
      className="relative group"
    >
      <div className="relative px-4 py-2 overflow-hidden rounded-xl">
        {/* Animated background on hover */}
        <div className="absolute inset-0 translate-y-[100%] group-hover:translate-y-[0%] transition-transform duration-300 bg-gradient-to-r from-blue-600/10 via-purple-600/10 to-pink-600/10"></div>
        
        {/* Text with hover effect */}
        <span className="relative z-10 text-gray-300 group-hover:text-white transition-colors duration-300">
          {children}
        </span>
        
        {/* Glowing border effect */}
        <div className="absolute inset-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
          <div className="absolute inset-x-0 h-[1px] top-0 bg-gradient-to-r from-transparent via-blue-500 to-transparent"></div>
          <div className="absolute inset-x-0 h-[1px] bottom-0 bg-gradient-to-r from-transparent via-purple-500 to-transparent"></div>
          <div className="absolute inset-y-0 w-[1px] left-0 bg-gradient-to-b from-transparent via-blue-500 to-transparent"></div>
          <div className="absolute inset-y-0 w-[1px] right-0 bg-gradient-to-b from-transparent via-purple-500 to-transparent"></div>
        </div>
      </div>
    </Link>
  );

  return (
    <div className="top-0 left-0 right-0 z-50">
      {/* Blur backdrop */}
      <div className={`absolute inset-0 transition-all duration-500 ${
        isScrolled ? 'backdrop-blur-lg bg-gray-900/70' : 'backdrop-blur-none bg-transparent'
      }`}></div>

      {/* Main header content */}
      <header className="relative">
        {/* Top gradient line */}
        <div className="absolute top-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-blue-500/50 to-transparent"></div>

        <div className="container mx-auto">
          <div className="relative flex justify-between items-center py-4 px-6">
            {/* Links Dropdown */}
            <div className="relative group z-20">
              <button
                onClick={() => setLinksDropdownOpen(!linksDropdownOpen)}
                className="relative group flex items-center space-x-2 px-6 py-2 rounded-xl bg-white/5 hover:bg-white/10 border border-white/10 hover:border-white/20 transition-all duration-300"
              >
                {/* Animated background */}
                <div className="absolute inset-0 rounded-xl opacity-0 group-hover:opacity-100 transition-opacity duration-300 bg-gradient-to-r from-blue-600/10 via-purple-600/10 to-pink-600/10"></div>
                
                <span className="relative text-gray-300 group-hover:text-white transition-colors duration-300">Links</span>
                <svg 
                  className={`w-4 h-4 text-gray-400 group-hover:text-white transition-all duration-300 ${
                    linksDropdownOpen ? 'rotate-180' : ''
                  }`}
                  fill="none" 
                  stroke="currentColor" 
                  viewBox="0 0 24 24"
                >
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7" />
                </svg>
              </button>

              {/* Dropdown menu */}
              {linksDropdownOpen && (
                <div className="absolute left-0 mt-2 w-72 animate-fadeIn">
                  <div className="relative rounded-xl overflow-hidden">
                    {/* Gradient border */}
                    <div className="absolute inset-0 p-[1px] rounded-xl bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500">
                      <div className="absolute inset-0 rounded-xl bg-gray-900/95 backdrop-blur-xl"></div>
                    </div>
                    
                    {/* Links content */}
                    <div className="relative">
                      <div className="p-2">
                        {links.map((link, index) => (
                          <a
                            key={link._id}
                            href={link.url}
                            className="group flex items-center space-x-3 px-4 py-3 rounded-lg hover:bg-white/5 transition-colors duration-300"
                          >
                            <div className="flex-shrink-0 w-8 h-8 rounded-lg bg-gradient-to-br from-blue-500/10 to-purple-500/10 flex items-center justify-center border border-white/10">
                              <svg className="w-4 h-4 text-gray-400 group-hover:text-white transition-colors duration-300" 
                                   fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                                      d="M10 6H6a2 2 0 00-2 2v10a2 2 0 002 2h10a2 2 0 002-2v-4M14 4h6m0 0v6m0-6L10 14" />
                              </svg>
                            </div>
                            <div className="flex-1 min-w-0">
                              <p className="text-sm text-gray-300 group-hover:text-white truncate transition-colors duration-300">
                                {link.url}
                              </p>
                              <p className="text-xs text-gray-500 group-hover:text-gray-400 transition-colors duration-300">
                                External link #{index + 1}
                              </p>
                            </div>
                            <div className="flex-shrink-0 opacity-0 group-hover:opacity-100 transition-opacity duration-300">
                              <svg className="w-5 h-5 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" 
                                      d="M9 5l7 7-7 7" />
                              </svg>
                            </div>
                          </a>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Logo */}
            <Link to="/" className="absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2 z-10">
              <div className="relative group">
                <div className="absolute -inset-1 bg-gradient-to-r from-blue-600 via-purple-600 to-pink-600 rounded-lg blur opacity-0 group-hover:opacity-50 transition duration-1000 group-hover:duration-300"></div>
                <h1 className="relative text-4xl font-black">
                  <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400">
                    Chat*S
                  </span>
                </h1>
              </div>
            </Link>

            {/* Navigation */}
            <nav className="flex-1 flex justify-end z-20">
              <ul className="flex items-center space-x-1">
                {isAuthenticated ? (
                  <>
                    <li><NavLink to="/">Dashboard</NavLink></li>
                    <li><NavLink to="/add-chat">Add Channels</NavLink></li>
                    <li><NavLink to="/join-chat">Join Chat</NavLink></li>
                    <li>
                      <NavLink to="/add-chats">
                        <div className="flex items-center space-x-2">
                          <span>Add Chats</span>
                          <span className="relative flex h-2 w-2">
                            <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-purple-400 opacity-75"></span>
                            <span className="relative inline-flex rounded-full h-2 w-2 bg-purple-500"></span>
                          </span>
                        </div>
                      </NavLink>
                    </li>
                  </>
                ) : (
                  <>
                    <li>
                      <Link to="/login" 
                            className="relative group px-6 py-2 rounded-xl overflow-hidden">
                        <div className="absolute inset-0 bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500"></div>
                        <span className="relative text-white font-medium">Login</span>
                      </Link>
                    </li>
                    <li>
                      <Link to="/register" 
                            className="relative group px-6 py-2 rounded-xl overflow-hidden bg-white/5 border border-white/10 hover:bg-white/10 hover:border-white/20 transition-all duration-300">
                        <span className="relative text-gray-300 group-hover:text-white transition-colors duration-300 font-medium">
                          Register
                        </span>
                      </Link>
                    </li>
                  </>
                )}
              </ul>
            </nav>
          </div>
        </div>

        {/* Bottom gradient line */}
        <div className="absolute bottom-0 left-0 right-0 h-[1px] bg-gradient-to-r from-transparent via-purple-500/50 to-transparent"></div>
      </header>
    </div>
  );
};

export default Header;
